INSERT INTO `wp_posts` VALUES('23061', '1', '2015-05-06 18:03:47', '2015-05-06 18:03:47', '<p style=\"color: #818181;\"><span style=\"color: #333300;\">Unique, enchanting and beautifully remote, Rancho Margot is a hidden gem near the Arenal Volcano and just 30-minutes from La Fortuna. Our Costa Rica eco-lodge in the rainforest is one of the country’s most exclusive getaways where boutique accommodation and farm-to-table dining go hand-in-hand with sustainable and self-sufficient practices.</span></p>\r\n<p style=\"color: #818181;\"><span style=\"color: #ff6600;\"><strong>Amenities</strong></span></p>\r\n<p style=\"color: #818181;\"><span style=\"color: #333300;\">Hot Water. Mini Refrigerator. Swimming Pool Kids Pool.Coffee Service.Jungle View.Game Room</span></p>\r\n<p style=\"color: #818181;\"><span style=\"color: #333300;\">Laundry Service.Conference Rooms.Currency Exchange.Private Parking.Fan.Lake View.Internet Access.Wireless Internet.Hiking Trails.CD Player.Restaurant.Hammocks.Video Collection.Balcony</span></p>\r\n<p style=\"color: #818181;\"><span style=\"color: #333300;\">Wet Bar.Babysitter.Gardens.Bar.Massage.Free Breakfast.Breakfast Available.Hot Springs.Yoga Lessons</span></p>\r\n<p style=\"color: #818181;\"><span style=\"color: #ff6600;\"><strong>Accommodations</strong></span></p>\r\n<p style=\"color: #818181;\"><span style=\"color: #333300;\">Fan . Hot Water.Wireless Internet.</span></p>\r\n<p class=\"large bold\" style=\"font-weight: 900; color: #3e220f;\"><span style=\"color: #ff6600;\">Bungalow</span></p>\r\n<p class=\"dark-grey\" style=\"color: #6d6d6d;\"> <span style=\"color: #333300;\">2 Queen bes 1 Queen + 2 Single.</span></p>\r\n<p class=\"dark-grey\" style=\"color: #6d6d6d;\"><span style=\"font-weight: inherit; font-style: inherit; color: #333300;\"><span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Gardens Views.</span> <span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Forest Views.</span> <span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Lake View.</span> <span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Porch</span> .<span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Includes 3 meals and 2 yoga lessons per day and a guided ranch tour.</span></span></p>\r\n<p style=\"color: #818181;\"><strong><span style=\"color: #ff6600;\">Bunkhouse</span></strong></p>\r\n<p class=\"dark-grey\" style=\"color: #6d6d6d;\"> <span style=\"color: #333300;\">2 in 1 Bedroom. 1 Bunk.</span></p>\r\n<p class=\"dark-grey\" style=\"color: #6d6d6d;\"><span style=\"font-weight: inherit; font-style: inherit; color: #333300;\"><span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Gardens Views.</span> <span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Forest Views.</span> <span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Lake Views.</span> <span class=\"horiz-dot-list\" style=\"font-weight: inherit; font-style: inherit;\">Shared bathroom</span></span></p>\r\n<p class=\"dark-grey\" style=\"color: #6d6d6d;\"><span style=\"color: #333333;\">[gravityform id=\"2\" title=\"true\" description=\"false\" ajax=\"true\"]</span></p>', 'Hotel Rancho Margot', '', 'publish', 'open', 'closed', '', 'hotel-rancho-margot', '', '', '2015-09-07 16:41:58', '2015-09-07 16:41:58', '', '0', 'http://www.mmexperiencecr.com/product/hotel-rancho-margot-2/', '0', 'product', '', '0');
