INSERT INTO `wp_posts` VALUES('23305', '4', '2015-07-11 03:38:29', '2015-07-11 03:38:29', '<span style=\"color: rgb(51, 51, 0);\">Hotel Giada is located on the Nicoya Peninsula, at Samara Beach.Playa Samara is a long, beautiful bay 7 km. considered one of the safest in Costa Rica because of the coral reef that protects it and makes it the ideal place to practice all water sports. In this small town, they enjoy a warm climate all year round, in a relaxed setting, surrounded by an incredible flora and fauna that make it a paradise for nature lovers. You can relax in the shade of their coconuts, caressed by the ocean breeze, admiring its colors ... appreciate wonderful sunsets that invite contemplation.</span>\r\n\r\n<span style=\"color: rgb(255, 102, 0);\"><strong>Amenities</strong></span>\r\n\r\n<span style=\"color: rgb(51, 51, 0);\">Air Conditioning.Hot Water.Swimming Poo.lKids Pool.Cable TV.Safe Deposit Box.Jacuzzi.Laundry Service.Currency Exchange.Private Parking.Internet Access.Wireless Internet.Restaurant.Hair Dryer.Room Service.Telephone.Balcony.Gardens.Bar.Handicap Accessible.Free Breakfast</span>\r\n\r\n<span style=\"color: rgb(255, 102, 0);\"><strong>Accommodations</strong></span>\r\n\r\n<span style=\"color: rgb(51, 51, 0);\">Wireless Internet .Telephone. Hair Dryer. Safe Deposit Box .Cable TV. Fan. Hot Water. Air Conditioning. Balcony.</span>\r\n\r\n<strong><span style=\"color: #ff6600;\">Standard Room</span></strong>\r\n<p class=\"dark-grey\" style=\"color: #6d6d6d;\"><span style=\"color: rgb(51, 51, 0);\">1 Bedroom. 2 Double (or) 1 King.Gardens Views</span></p>\r\n[gravityform id=\"2\" title=\"true\" description=\"false\"]', 'Hotel Giada', '', 'publish', 'open', 'closed', '', 'hotel-giada', '', '', '2015-09-12 16:15:27', '2015-09-12 16:15:27', '', '0', 'http://www.mmexperiencecr.com/?post_type=product&#038;p=23305', '0', 'product', '', '0');
