INSERT INTO `wp_posts` VALUES('23552', '4', '2015-07-31 20:21:45', '2015-07-31 20:21:45', '<span style=\"color: #333300;\">The Balmoral is a lively, full service, landmark hotel located on the famous Central Avenue in the historical and vibrant heart of downtown San Jose, Costa Rica and only 10 miles away from Juan Santamaria International Airport (SJO). The Balmoral is ideal for both leisure and business travelers.</span>\r\n\r\n<span style=\"color: #333300;\">Within walking distance of the hotel, you will find San Jose\'s main art, cultural, financial, government and historical landmarks, as well as some of city\'s best entertainment, shopping, and healthcare facilities. From the hotel, you can also take a wide variety of one-day tours to discover Costa Rica\'s natural beauties.</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Amenities</strong></span>\r\n\r\n<span style=\"color: #333300;\">Air Conditioning .Hot Water .Cable TV. Coffee Service. Safe Deposit Box.Laundry ServiceConference Rooms. Currency Exchange .Private Parking. Internet Access. Wireless InternetRestaurant. Hair Dryer. Telephone. Mini Bar. Balcony. Babysitter.Bar.Handicap AccessibleFree Breakfast .Full Size .Gym. Alarm Clock. Massage. Mini Gym. Breakfast Available .Room Service</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Accommodations </strong></span>\r\n\r\n<span style=\"color: #333300;\">Wireless Internet Telephone Hair Dryer Safe Deposit Box Cable TV Hot Water Air Conditioning Mini Bar</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Deluxe Room</strong></span>\r\n\r\n<span style=\"color: #333300;\">2 Double bed.Alarm Clock. Sleeps 4 in 1 Bedroom</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Familiar Room</strong></span>\r\n\r\n<span style=\"color: #333300;\">3 Double bed .Alarm Clock Living Room. Sleeps 6 in 2 Bedrooms.</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Standard Room</strong></span>\r\n\r\n<span style=\"color: #333300;\">2 Single bed (or) 2 Double bed (or) 1 Double.Wheelchair Accessible. Sleeps 3 in 1 Bedroom</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Suite</strong></span>\r\n\r\n<span style=\"color: #333300;\">1 King bed .Alarm Clock Living Room Jacuzzi. Sleeps 2 in 1 Bedroom</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Superior Room</strong></span>\r\n\r\n<span style=\"color: #333300;\">2 Double bed.Alarm Clock. Sleeps 4 in 1 Bedroom.</span>\r\n\r\n[gravityform id=\"1\" title=\"true\" description=\"false\"]', 'Hotel Balmoral', '', 'publish', 'open', 'closed', '', 'hotel-balmoral', '', '', '2015-09-26 01:56:37', '2015-09-26 01:56:37', '', '0', 'http://www.mmexperiencecr.com/?post_type=product&#038;p=23552', '0', 'product', '', '0');
