INSERT INTO `wp_posts` VALUES('23690', '4', '2015-09-13 06:25:23', '2015-09-13 06:25:23', '<p id=\"tw-target-text\" class=\"tw-data-text vk_txt tw-ta tw-text-small\" dir=\"ltr\" style=\"color: #212121;\" data-placeholder=\"Traducción\"><span lang=\"es\">Prepárese para obtener la adrenalina, Cavernas de Venado en Costa Rica se está convirtiendo en una visita rápida atracción para todas las edades! Vas a estar arrastrándose a lo largo de los sistemas de ríos subterráneos en cámaras de cuevas solitarias donde tendrá una rara oportunidad de fijar sus ojos en formaciones rocosas del Paleolítico únicos y hermosos . Visita las Cavernas de Venado y caminar a través de los caminos de paso suave , piedra subterráneos.</span></p>\n<span style=\"color: #ff6600;\"><strong>Price  : $ 80      minimum:  2 people           time : 7:30 am, 1:00 pm</strong></span>\n\n[gravityform id=\"2\" title=\"true\" description=\"false\" ajax=\"true\"]', 'Cavernas de Venado', '', 'inherit', 'open', 'open', '', '23112-autosave-v1', '', '', '2015-09-13 06:25:23', '2015-09-13 06:25:23', '', '23112', 'http://www.mmexperiencecr.com/2015/08/01/23112-autosave-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES('23928', '4', '2015-08-19 19:19:29', '2015-08-19 19:19:29', '<span style=\"color: #333300;\">Among the original laid-back caribbean villages of Puerto Viejo and Manzanillo, (in the South Caribbean Coast of Costa Rica), right in front of Cocles Beach, an enchanting and serene lodge, in complete harmony with the surrounding tropical rainforest, awaits you at Azania Bungalows.For enjoying a delightful tropical breakfast, fresh drinks, listen to some music, Direct TV, play games or get some information and advices about tours and activities…a cozy place with fine woods and inviting decoration awaits you… the “Club House / Reception”…built like a typical palm-roof Rancho which reflects in the dimensions of its structure those of the surrounding rainforest.</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Amenities</strong></span>\r\n\r\n<span style=\"color: #333300;\">Swimming Pool.Private Parking.Restaurant.Bar.Beach Access.Breakfast Available.Cable TV.Coffee Service.Fan.Free Breakfast.Jacuzzi.Internet Access.Hot Water.Hammocks.Gardens.Laundry Service.Safe Deposit Box.</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong> </strong><strong>Accommodations</strong></span>\r\n\r\n<span style=\"color: #333300;\">Cable TV. Fan. Hot Water .Mini Bar. Safe Deposit Box.</span>\r\n\r\n<span style=\"color: #ff6600;\"><strong>Bungalow</strong></span>\r\n\r\n<span style=\"color: #333300;\">1 Queen + 2 Twin. 4people  in 1 Bedroom</span>\r\n\r\n[gravityform id=\"2\" title=\"true\" description=\"false\" ajax=\"true\"]\r\n\r\n<strong> </strong>', 'Azania  bungalows', '', 'publish', 'open', 'closed', '', 'azania-bungalows-2', '', '', '2015-09-21 01:20:49', '2015-09-21 01:20:49', '', '0', 'http://www.mmexperiencecr.com/?post_type=product&#038;p=23928', '0', 'product', '', '0');
