INSERT INTO `wp_posts` VALUES('24218', '4', '2015-09-15 16:26:01', '2015-09-15 16:26:01', '<span style=\"color: #333300;\">Experience the magic of the most exotic nocturnal creatures on the jungle night walk at the wild life refuge of Si Como No. There visitors will have the opportunity to see local reptiles, toads, insects and tree frogs, while immersed in the raucous symphony that fills the night air of this tropical jungle sanctuary</span>\n\n<span style=\"color: #333300;\">Jungle Night Walk a unique odyssey into the nocturnal world of the tropical rain forest. Visit the amphibian water gardens and listen to the nightlife of over dozen species of frogs. Exhibits - Amphibian Aquatic Garden The Amphibian Aquatic Nocturnal Garden is a series of waterways and pools ecologically design to provide the different species with an ideal year around habitat to reproduce.</span>\n\n<span style=\"color: #ff6600;\"><strong>Hour :</strong></span> <span style=\"color: #333300;\"> 5:30 pm             </span>\n\n<strong><span style=\"color: #ff6600;\">Requirements  :</span> </strong>  <span style=\"color: #333300;\">Good for all ages – Comfortable Clothes, and Camera.</span>\n\n<strong><span style=\"color: #ff6600;\">Includes:</span></strong> <span style=\"color: #333300;\"> Entrance fee and bilingual interpretive guides and Round Trip Transportation.</span>', 'The Jungle Night Walk', '', 'inherit', 'open', 'open', '', '24211-autosave-v1', '', '', '2015-09-15 16:26:01', '2015-09-15 16:26:01', '', '24211', 'http://www.mmexperiencecr.com/2015/09/15/24211-autosave-v1/', '0', 'revision', '', '0');
