INSERT INTO `wp_posts` VALUES('24377', '4', '2015-09-25 22:14:45', '2015-09-25 22:14:45', '<span style=\"color: #333300;\">Located less than 10 minutes away from the Monteverde Cloud Forest Biological Preserve and in a walk-able distance from Santa Elena Downtown, Monteverde Country Lodge is the perfect option for those who are looking for cozy lodging, nice rest and easy access to all the activities Monteverde has to offer.</span>\n\n<span style=\"color: #333300;\">The grounds, amenities and infrastructure create an experience of authentic Costa Rican hospitality. The lodge caters to offer personalized attention, excellence in costumer service and authentic Monteverde hospitality.</span>\n\n<span style=\"color: #ff6600;\"><strong>Amenities</strong></span>\n\n<span style=\"color: #333300;\">Cable Tv,Bar ,Free Breakfast, GardensHot Water ,Internet Access, Laundry Service, Private Parking, Restaurant ,Wireless Internet, Telephone, Safe Deposit Box</span>\n\n<span style=\"color: #ff6600;\"><strong>Accomodations</strong></span>\n\n<span style=\"color: #333300;\"><span class=\"horiz-dot-list\">Safe Deposit Box</span> , <span class=\"horiz-dot-list\">Cable TV ,</span> <span class=\"horiz-dot-list\">Hot Water ,</span> <span class=\"horiz-dot-list\">Wireless Internet ,</span><span class=\"horiz-dot-list\">Telephone.</span></span>\n<p class=\"large bold\"><span style=\"color: #ff6600;\"><strong>Superior Room</strong></span></p>\n<p class=\"dark-grey\"><span style=\"color: #333300;\"> 2 Queen beds  (or) 1 Queen + 1 Single.<span class=\"horiz-dot-list\">Forest Views</span></span></p>\n<p class=\"large bold\"><span style=\"color: #ff6600;\"><strong>Classic Room</strong></span></p>\n<p class=\"dark-grey\"><span style=\"color: #333300;\"> 1 Double beds  (or) 2 Double.<span class=\"horiz-dot-list\">Forest Views</span></span></p>\n[gravityform id=\"2\" title=\"true\" description=\"true\"]\n\n&nbsp;\n\n&nbsp;\n\n&nbsp;', 'Monteverde Country Lodge', '', 'inherit', 'closed', 'closed',